  state = {
    todos: [
      {
        id: 1,
        title: 'Take out the trash',
        completed: false
      },
      {
        id: 1,
        title: 'Take out the trash',
        completed: false
      }, {
        id: 1,
        title: 'Take out the trash',
        completed: false
      }, {
        id: 1,
        title: 'Take out the trash',
        completed: false
      }
    ]
  }

  render() {
    return (
      <div className="App">
        <Todos />
      </div>
    );
  }
}

